INSERT INTO `wp_posts` VALUES("428", "13", "2015-05-28 08:03:18", "2015-05-28 12:03:18", "<strong>This meeting is open to</strong>\r\nALL\r\npeople who live and serve at Ananda. Staff, residents,\r\nmanagement, volunteers, teachers, immersion program\r\nparticipants...all are welcome to participate in the forum.\r\n\r\nThe community meeting is an opportunity for us to gather in love\r\nand support for one another.\r\n\r\nThe community meeting is designed and hosted by some of the Ananda\r\nAshram Staff Members, and is not associated with management. It is\r\na place for us to share ideas, projects, services.\r\n\r\nIt is a safe space to discuss challenges and engage in the\r\nsynergistic, creative problem solving process that a group setting\r\ncan provide.\r\n\r\nIt is a space to discuss topics that affect the community may not\r\nbe covered in our monthly staff meetings.\r\n\r\nAs a community we collectively have so many resources, skills and\r\nexperience. The community meeting is a platform for us to\r\nbring together our collective brilliance in realizing Guruji\'s\r\nvision of Ananda Ashram being “the abode of heaven on\r\nearth.”\r\n\r\n<strong>What we will be discussing in this meeting:</strong>\r\n<ul>\r\n	<li>Shanna- Short reading from the guru\'s writings</li>\r\n	<li>Open discussion on ground rules and meeting format</li>\r\n	<li>Karen- The importance of accountability and boundaries in community\r\nspace</li>\r\n	<li>Schedule next meeting</li>\r\n</ul>\r\n<strong><em>If you have a topic you would like to bring to the forum please</em></strong>\r\n<strong><em> contact Shanna or Karen before Sunday so that we can add it to the</em></strong>\r\n<strong><em> agenda and be sure you have time to discuss your topic. In respect</em></strong>\r\n<strong><em> of others and their time, you must have your topic on the agenda to</em></strong>\r\n<strong><em> discuss it in the community forum.</em></strong>", "Ananda Community Meeting", "", "publish", "open", "closed", "", "ananda-community-meeting", "", "", "2015-05-28 08:05:01", "2015-05-28 12:05:01", "", "0", "http://www.internal.anandaashram.org/?post_type=ai1ec_event&#038;p=428", "0", "ai1ec_event", "", "0");
INSERT INTO `wp_posts` VALUES("429", "13", "2015-05-28 08:04:42", "2015-05-28 12:04:42", "<strong>This meeting is open to</strong>\nALL\npeople who live and serve at Ananda. Staff, residents,\nmanagement, volunteers, teachers, immersion program\nparticipants...all are welcome to participate in the forum.\n\nThe community meeting is an opportunity for us to gather in love\nand support for one another.\n\nThe community meeting is designed and hosted by some of the Ananda\nAshram Staff Members, and is not associated with management. It is\na place for us to share ideas, projects, services.\n\nIt is a safe space to discuss challenges and engage in the\nsynergistic, creative problem solving process that a group setting\ncan provide.\n\nIt is a space to discuss topics that affect the community may not\nbe covered in our monthly staff meetings.\n\nAs a community we collectively have so many resources, skills and\nexperience. The community meeting is a platform for us to\nbring together our collective brilliance in realizing Guruji\'s\nvision of Ananda Ashram being “the abode of heaven on\nearth.”\n\n<strong>What we will be discussing in this meeting:</strong>\n<ul>\n	<li>Shanna- Short reading from the guru\'s writings</li>\n	<li>Open discussion on ground rules and meeting format</li>\n	<li>Karen- The importance of accountability and boundaries in community\nspace</li>\n	<li>Schedule next meeting</li>\n</ul>\nIf you have a topic you would like to bring to the forum please\ncontact Shanna or Karen before Sunday so that we can add it to the\nagenda and be sure you have time to discuss your topic. In respect\nof others and their time, you must have your topic on the agenda to\ndiscuss it in the community forum.", "Ananda Community Meeting", "", "inherit", "open", "closed", "", "428-autosave-v1", "", "", "2015-05-28 08:04:42", "2015-05-28 12:04:42", "", "428", "http://www.internal.anandaashram.org/428-autosave-v1/", "0", "revision", "", "0");
